@article{doi:10.1175/2008JHM1068.1,
author = {Balsamo, Gianpaolo and Beljaars, Anton and Scipal, Klaus and Viterbo, Pedro and van den Hurk, Bart and Hirschi, Martin and Betts, Alan K.},
title = {A Revised Hydrology for the ECMWF Model: Verification from Field Site to Terrestrial Water Storage and Impact in the Integrated Forecast System},
journal = {Journal of Hydrometeorology},
volume = {10},
number = {3},
pages = {623-643},
year = {2009},
doi = {10.1175/2008JHM1068.1},

URL = { 
        https://doi.org/10.1175/2008JHM1068.1

},
eprint = { 
        https://doi.org/10.1175/2008JHM1068.1

}
,
    abstract = { Abstract The Tiled ECMWF Scheme for Surface Exchanges over Land (TESSEL) is used operationally in the Integrated Forecast System (IFS) for describing the evolution of soil, vegetation, and snow over the continents at diverse spatial resolutions. A revised land surface hydrology (H-TESSEL) is introduced in the ECMWF operational model to address shortcomings of the land surface scheme, specifically the lack of surface runoff and the choice of a global uniform soil texture. New infiltration and runoff schemes are introduced with a dependency on the soil texture and standard deviation of orography. A set of experiments in stand-alone mode is used to assess the improved prediction of soil moisture at the local scale against field site observations. Comparison with basin-scale water balance (BSWB) and Global Runoff Data Centre (GRDC) datasets indicates a consistently larger dynamical range of land water mass over large continental areas and an improved prediction of river runoff, while the effect on atmospheric fluxes is fairly small. Finally, the ECMWF data assimilation and prediction systems are used to verify the effect on surface and near-surface quantities in the atmospheric-coupled mode. A midlatitude error reduction is seen both in soil moisture and in 2-m temperature. }
}

@article{doi:10.1175/JHM-382.1,
author = {Best, M. J. and Beljaars, A. and Polcher, J. and Viterbo, P.},
title = {A Proposed Structure for Coupling Tiled Surfaces with the Planetary Boundary Layer},
journal = {Journal of Hydrometeorology},
volume = {5},
number = {6},
pages = {1271-1278},
year = {2004},
doi = {10.1175/JHM-382.1},

URL = { 
        https://doi.org/10.1175/JHM-382.1

},
eprint = { 
        https://doi.org/10.1175/JHM-382.1

}
,
    abstract = { Abstract A generalized coupling is proposed between atmospheric models and surface schemes (land and ocean). A set of input and output variables is defined for this purpose in such a way that it can be used by many current and future models, including mosaic or tile schemes. The basic concept is to pass atmospheric variables from the lowest model level and their relation to corresponding fluxes to the surface scheme. The surface scheme returns the fluxes. In this framework, there is no need for the atmospheric model to have detailed information about the surface. Only the result of the surface computations is needed; namely, the fluxes, which are applied as a boundary condition. The equations for fully implicit coupling are derived, and the relevance for numerical stability is demonstrated. It is also shown that fully implicit coupling in a tile scheme leads to more robust results than partially implicit coupling. }

